function getTaoGoods(b,a){if(!a){a=8;}$.post(_basePath+"taoGoods.htm?t="+new Date().getTime(),{keyword:b,pageSize:a},function(c){$("#tao").css({"background-image":"none"});var d="";$.each(c,function(e,f){var g=(Math.ceil(f.discount*100)/10).toFixed(1);d+='<div class="tao-mod"><h3 class="tit"><a href="'+f.sourceUrlFor3w+'" target="_blank">'+f.name+'</a></h3><div class="pic"><a href="'+f.sourceUrlFor3w+'" target="_blank"><img src="'+f.imgurl+'" />'+'</a><div class="original"><span class="del">原价：&yen;'+f.sourcePrice+'</span><span class="zhe">'+g+'折</span><span class="spc"><b>'+f.totalClick+"</b>人已购</span></div></div>"+'<div class="now"><span class="price"><b>&yen;</b>'+f.salesPrice+'</span><a href="'+f.sourceUrlFor3w+'" target="_blank" class="btn">去抢购</a></div></div>';});if(d!=""){$(".tao-bd").append(d);$("#tao").show();}else{$("#tao").css({"display":"none"});}}).error(function(){$("#tao").css({"display":"none"});});}